та  -->    <div class="row">        <div class="col-md-6 col-md-offset-5">            <button class="btn btn-success" id="add_todo" data-toggle="modal" data-target="#modaladd">Add TODO LIST</button>        </div>    </div>    <!--modal add -->    <div class="modal fade" id="modaladd" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true" >        <div class="modal-dialog">            <div class="modal-content">                <div class="modal-header">                    <button type="button" class="close" data-dismiss="modal"><span aria-hidden="true">&times;</span><span class="sr-only">Close</span></button>                    <h4 class="modal-title" id="myModalLabel">Add todo list</h4>                    <p style="text-align: center" class="bg-danger msg_error"></p>                </div>                <div id="my-content" class="modal-body">                        <div class="row">                            <div class="form-group">                                <label class="control-label" for="copmapny">Add todo list</label>                                <div class="col-xs-12">                                    <input type="text" placeholder="name todo list" class="form-control" name="name_todo_list"                                           value="" required="required">                                </div>                            </div>                            <br>                            <br>                            <div class="form-group">                                <div class="col-xs-6">                                    <button id="save_todo" type="button" class="btn btn-primary btn-lg btn-block" name="save_todo">Save                                    </button>                                </div>                            </div>                        </div>                </div>            </div>        </div>    </div>    <!-- end modal add --></div>